import {useRef, useState} from 'react';

import classes from './Checkout.module.css';

const isEmty = value => value.trim() === '';
const isFiveChars = value => value.trim().length === 5;

const Checkout = (props) => {
    const [formInputsValidity, setFormInputsValidity] = useState({
        name: true,
        street: true,
        city: true,
        postalCode: true

    })
    const nameInputRef = useRef();
    const streetInputRef = useRef();
    const postalInputRef = useRef();
    const cityInputRef = useRef();

    const confirmHandler = (e) => {
        e.preventDefault();

        const enteredName = nameInputRef.current.value;
        const enteredStreet = streetInputRef.current.value;
        const enteredPostalCode = postalInputRef.current.value;
        const enteredCity = cityInputRef.current.value;

        const enteredNameIsValid = !isEmty(enteredName);
        const enteredStreetIsValid = !isEmty(enteredStreet);
        const enteredCityIsValid = !isEmty(enteredCity)
        const enterdPostalCodeIsValid = isFiveChars(enteredPostalCode);

        const formIsValid =
            enteredNameIsValid &&
            enteredStreetIsValid &&
            enteredCityIsValid &&
            enterdPostalCodeIsValid;

            if(!formIsValid) {
                return;
                // submit
            }
    };

    return (
        <form onSubmit={confirmHandler}>  
            <div className={classes.control}>
               <label htmlFor='name' ref={nameInputRef}>Ton Non</label> 
               <input type='text' />
            </div>
            <div className={classes.control}>
                <label htmlFor="street">Route</label>
                <input type='text' id='street' ref={streetInputRef} />
            </div>
            <div className={classes.control}>
                <label htmlFor="postal">Postal Code</label>
                <input type='text' id='postal' ref={postalInputRef} />
            </div>
            <div className={classes.control}>
                <label htmlFor="postal">Ville</label>
                <input type='text' id='city' ref={cityInputRef} />
            </div>
            <button type="button" onClick={props.onCancel}>Cancel</button>
            <button>Confirm</button>
        </form>
    )
}
export default Checkout;
